posts_by_date.map:
function(doc) {
  if (doc.post)
  {
    var parentId = doc.parent_id==undefined?"":doc.parent_id;
    emit(parentId  + doc._id, 
      { "pubDate": new Date(doc.pub_date[0], doc.pub_date[1], doc.pub_date[2], doc.pub_date[3], doc.pub_date[4], doc.pub_date[5]), 
        "summary":doc.summary, 
        "post":doc.post,
       "parent_id":doc.parent_id,
       "level":doc.level
      })
  }
};
